int Test(vector<Question> A, vector<int>& Module, vector<int>& Erreurs, vector<string>& Reponse_donnee, vector<string>& Reponse_attendue, vector<Tableau_Final>& Resume, int nombre_question_module, int nombre_question)
{
    int random; int score = 0; string reponse_question; int ordreCH; int ordreQ;
    for (int i = 0; i < nombre_question; i++)
    {
        do
        {
            random = rand() % (nombre_question_module - 1);
        } while (A[random].Passee == 1);
        cout << "Cette question se refere a la question numero " << A[random].Module << "." << A[random].identifiant << " du livre\n\n" << i + 1 << ". " << A[random].Enonce << "\n\n\nVeuillez rentrer votre reponse" << endl;
        cin >> reponse_question;

        if (reponse_question == "PASSEALE") //Change de question
        {
            do
            {
                random = rand() % (nombre_question_module - 1);
            } while (A[random].Passee == 1);
            system("CLS");
            cout << "Cette question se refere a la question numero " << A[random].Module << "." << A[random].identifiant << " du livre\n\n" << i + 1 << ". " << A[random].Enonce << "\n\n\nVeuillez rentrer votre reponse" << endl;
            cin >> reponse_question;
        }

        if (reponse_question == "PASSE")  //Choix de question
        {
            cout << "\nVous voulez passer la question, quel est le chaptitre de celle-ci ?\n" << endl;
            cin >> ordreCH;
            cout << "\n\nLe numero ?\n" << endl;
            cin >> ordreQ;
            for (unsigned int l = 0; l < A.size(); l++)
            {
                if (A[l].identifiant == ordreQ && A[l].Module == ordreCH)
                {
                    random = l;
                }
            }
            system("CLS");
            cout << "Cette question se refere a la question numero " << A[random].Module << "." << A[random].identifiant << " du livre\n\n" << i + 1 << ". " << A[random].Enonce << "\n\n\nVeuillez rentrer votre reponse" << endl;
            cin >> reponse_question;
        }
        score = score + Correction(reponse_question, A[random].reponse);
        if (Correction(reponse_question, A[random].reponse) != 1)
        {
            Erreurs.push_back(A[random].identifiant); Module.push_back(A[random].Module);
            Reponse_donnee.push_back(reponse_question); Reponse_attendue.push_back(A[random].reponse);
            cout << "\nLa reponse n'est pas correcte.\nLa bonne reponse est : " << A[random].reponse << "\n" << endl;
        }
        if (Correction(reponse_question, A[random].reponse) == 1) { cout << "\nBravo, la reponse est correcte !\n" << endl; }
        A[random].Passee = 1;
        Decalage(Resume, random, A[random].Module);
        system("PAUSE");
        system("CLS");
    }
    return score;
}

void Module(vector<Question>& A, int n)
{
    for (unsigned int i = 0; i < A.size(); i++)
    {
        A[i].Module = n;
    }
}

vector<Question> Trois_Chapites(int x, int q, int v, vector<Question> a, vector<Question> b, vector<Question> c, vector<Question> d, vector<Question> e, vector<Question> f, vector<Question> g, vector<Question> h, vector<Question> i, vector<Question> j, vector<Question> k)
{
    vector<Question> Combinaison;
    Combinaison.reserve(1027); //Nombre max de question possibles dans les 3 tests
    if (x == a[0].Module || q == a[0].Module || v == a[0].Module) { Combinaison.insert(Combinaison.end(), a.begin(), a.end()); }
    if (x == b[0].Module || q == b[0].Module || v == b[0].Module) { Combinaison.insert(Combinaison.end(), b.begin(), b.end()); }
    if (x == c[0].Module || q == c[0].Module || v == c[0].Module) { Combinaison.insert(Combinaison.end(), c.begin(), c.end()); }
    if (x == d[0].Module || q == d[0].Module || v == d[0].Module) { Combinaison.insert(Combinaison.end(), d.begin(), d.end()); }
    if (x == e[0].Module || q == e[0].Module || v == e[0].Module) { Combinaison.insert(Combinaison.end(), e.begin(), e.end()); }
    if (x == f[0].Module || q == f[0].Module || v == f[0].Module) { Combinaison.insert(Combinaison.end(), f.begin(), f.end()); }
    if (x == g[0].Module || q == g[0].Module || v == g[0].Module) { Combinaison.insert(Combinaison.end(), g.begin(), g.end()); }
    if (x == h[0].Module || q == h[0].Module || v == h[0].Module) { Combinaison.insert(Combinaison.end(), h.begin(), h.end()); }
    if (x == i[0].Module || q == i[0].Module || v == i[0].Module) { Combinaison.insert(Combinaison.end(), i.begin(), i.end()); }
    if (x == j[0].Module || q == j[0].Module || v == j[0].Module) { Combinaison.insert(Combinaison.end(), j.begin(), j.end()); }
    if (x == k[0].Module || q == k[0].Module || v == k[0].Module) { Combinaison.insert(Combinaison.end(), k.begin(), k.end()); }
    return Combinaison;
}

vector<Question> General(vector<Question> a, vector<Question> b, vector<Question> c, vector<Question> d, vector<Question> e, vector<Question> f, vector<Question> g, vector<Question> h, vector<Question> i, vector<Question> j, vector<Question> k, vector<Question> l)
{
    vector<Question> Final;
    Final.reserve(a.size() + b.size() + c.size() + d.size() + e.size() + f.size() + g.size() + h.size() + i.size() + j.size() + k.size() + l.size());
    Final.insert(Final.end(), a.begin(), a.end());
    Final.insert(Final.end(), b.begin(), b.end());
    Final.insert(Final.end(), c.begin(), c.end());
    Final.insert(Final.end(), d.begin(), d.end());
    Final.insert(Final.end(), e.begin(), e.end());
    Final.insert(Final.end(), f.begin(), f.end());
    Final.insert(Final.end(), g.begin(), g.end());
    Final.insert(Final.end(), h.begin(), h.end());
    Final.insert(Final.end(), i.begin(), i.end());
    Final.insert(Final.end(), j.begin(), j.end());
    Final.insert(Final.end(), k.begin(), k.end());
    Final.insert(Final.end(), l.begin(), l.end());
    return Final;
}
